package com.class7;

public class ShorthandExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=0;
		a+=100;
		System.out.println(a);
		
		int b=75;
		b-=67;
		
		System.out.println(b);
		
		double cakePiece=11;
		cakePiece/=4;
		System.out.println(cakePiece);
		
		int cakePiece1=25;
		cakePiece1%=7;
		System.out.println(cakePiece1);
	}

}
